sys_script_include.script:
var GetEmailAddress = Class.create();
GetEmailAddress.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {

	getEmail:function(){

		//provide the email address of the user by passing his/her user_id/sys_id 
		var grUser = new GlideRecord('sys_user');
		grUser.addQuery('sys_id', this.getParameter('sysparm_userID'));
		grUser.query();
		if(grUser.next()){
			return grUser.email + '';
		}
	},

    type: 'GetEmailAddress'
});
